% ASA implementation in Experiment%% setup startXs = [0.4 0.9]; % Start values (e.g., an easy and a difficult contrast) terminate = 30; phi = 0.75; % converges to stepCon = 0.5; % Initial step size for j = 1:length(startXs) % set up x amount of staircases    ASA(j) = ASASetup(phi,startXs(j),stepCon,terminate); end   %% in experiment  j = current_stair; % choose which staircase you are using on this trial  temp_con=1-ASA(j).X(end);      % choose contrast (last contrast obtained in this staircase)  % display some stuff with said contrast  coranswer = response_id == target_id; % check for correct answer (differs per experiment)  ASA(j) = ASAUpdate(ASA(j),coranswer); % update your staircaseif ASA(j).X(end)<0;   % limit contrast to sensible values (differs per experiment)    ASA(j).X(end)=0; elseif ASA(j).X(end)> 0.99;     ASA(j).X(end)=0.99; end    